import React from 'react'
import styled from 'styled-components'
import {
  LayoutHead,
  LayoutHeder,
  LayoutBody,
  LayoutFooter
} from '../components/layout'

const Container = styled.div`
  margin: 0 auto;
  display: grid;
  grid-template:
    'header heder' 50px
    'main   main' 300px
    'main   main' 300px
    'footer footer ' 50px /
    50px 1fr 50px;

  .header {
    grid-area: header;
    background-color: white;
    position: fixed;
    width: 100%;
    left: 0;
    top: 50;
  }
  .main {
    grid-area: main;
    /* background-color: royalblue; */
    /* position: static; */
    /* position: fixed; */
    padding-top: 4rem;
    width: 100%;
  }
  .seccion {
    grid-area: seccion;
    background-color: azure;
    padding-top: 16rem;
    width: 100%;

    /* position:relative; */
  }
  .footer {
    grid-area: footer;
    background-color: rosybrown;
    margin-top: 2rem;
    /* position:relative; */
  }

  @media (min-width: 600px) {
    .contenedor {
      grid-template:
        'header   header   header' 150px
        'main     main     main' 1fr
        'footer   footer   footer' 130px /
        200px auto 200px;
    }
  }
  @media (min-width: 900px) {
    .contenedor {
      grid-template:
        'header   header   header     header' 100px
        'main     main     main       main' 1fr
        'footer   footer   footer     footer' 130px /
        200px 1fr 1fr 200px;

      margin: 0px 50px;
    }
  }
`

const Home: React.FC = () => {
  return (
    <>
      <Container>
        <LayoutHead />
        <div className="heder">
          <LayoutHeder />
        </div>
        <div className="main">
          <LayoutBody />
        </div>
        <div className="footer">
          <LayoutFooter />
        </div>
      </Container>
    </>
  )
}

export default Home
